select smn_comercial.smn_documento_grp_titulo_imp.smn_documento_grp_titulo_imp_id as id, smn_comercial.smn_documento.dcf_codigo || ' - ' || smn_comercial.smn_documento.dcf_descripcion || ' - ' || smn_base.smn_grupo_titulo_impresion.gti_codigo || ' - ' || smn_base.smn_grupo_titulo_impresion.gti_descripcion as item from  smn_comercial.smn_documento_grp_titulo_imp, smn_comercial.smn_documento, smn_base.smn_grupo_titulo_impresion where smn_comercial.smn_documento_grp_titulo_imp.smn_documento_id = smn_comercial.smn_documento.smn_documento_id and smn_comercial.smn_documento_grp_titulo_imp.smn_grupo_titulo_impresion_rf = smn_base.smn_grupo_titulo_impresion.smn_grupo_titulo_impresion_id order by smn_grupo_titulo_impresion_rf