select smn_base.smn_tasas_de_cambio.smn_tasas_de_cambio_id as id, smn_base.smn_monedas.mon_codigo ||' ->'|| smn_base.smn_tasas_de_cambio.tca_tasa_cambio as item,  
smn_base.smn_tasas_de_cambio.tca_tasa_cambio as tasa_cambio from smn_base.smn_tasas_de_cambio left outer join smn_base.smn_monedas on smn_base.smn_monedas.smn_monedas_id = smn_base.smn_tasas_de_cambio.smn_monedas_id
where smn_base.smn_tasas_de_cambio.smn_monedas_id=${fld:id} and smn_base.smn_tasas_de_cambio.tca_tipo_tasa=(select smn_comercial.smn_documento.tca_tipo_tasa from smn_comercial.smn_documento where smn_comercial.smn_documento.smn_documento_id = (SELECT smn_comercial.smn_centro_facturacion.smn_documento_id FROM smn_comercial.smn_centro_facturacion where smn_comercial.smn_centro_facturacion.smn_centro_facturacion_id = ${fld:centro})) and smn_base.smn_tasas_de_cambio.tca_fecha_vigencia = current_date and smn_base.smn_tasas_de_cambio.tca_estatus='AC' limit 1