select smn_comercial.smn_ingredientes.smn_ingredientes_id as smn_ingredientes_id, smn_base.smn_item.itm_nombre as item from smn_comercial.smn_rel_detalle_ingrediente
inner join smn_comercial.smn_ingredientes on smn_comercial.smn_ingredientes.smn_ingredientes_id = smn_comercial.smn_rel_detalle_ingrediente.smn_ingredientes_id
inner join smn_comercial.smn_solicitud_servicio_entregas_detalle on smn_comercial.smn_solicitud_servicio_entregas_detalle.smn_solicitud_servicio_entregas_detalle_id = smn_comercial.smn_rel_detalle_ingrediente.smn_solicitud_servicio_entregas_detalle_id
inner join smn_base.smn_item on smn_base.smn_item.smn_item_id = smn_comercial.smn_ingredientes.smn_solicitud_servicio_entregas_detalle_id
where smn_comercial.smn_rel_detalle_ingrediente.smn_solicitud_servicio_entregas_detalle_id=${fld:id}